import './App.css';
import Users from './component/Users';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import ChatConsole from './component/ChatConsole';
import JoinChat from './component/JoinChat';
import FileUpload from './component/FileUpload';
import { StompProvider } from './proxy/StompProvider';

function App() {
  return (
    <div className="container mt-4">
      <h4 className="text-center">Chat Application</h4>

      <StompProvider>
        <Router>
          <Routes>
            <Route path="/" element={<JoinChat />} />
            <Route
              path="/chat-console/:username"
              element={
                <div className="row">
                  <Users />
                  <ChatConsole />
                </div>
              }
            />
            {/* Nouvelle route pour la gestion des fichiers */}
            <Route path="/files" element={<FileUpload />} />
          </Routes>
        </Router>
      </StompProvider>
    </div>
  );
}

export default App;
