import React, { useState, useEffect } from "react";
import Profile from "./Profile";
import { useParams } from "react-router-dom";
import { useMessageContext } from "../proxy/StompProvider";
import { uploadFile } from "../component/fileService";
import axios from "axios";

const ChatConsole = () => {
  const { username } = useParams();
  const { publishMessage } = useMessageContext();
  const [messages, setMessages] = useState([]);
  const [content, setContent] = useState("");
  const [file, setFile] = useState(null);

  // Charger les messages depuis le backend
  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const response = await axios.get("/api/messages");
        setMessages(response.data);
      } catch (error) {
        console.error("Error fetching messages:", error);
      }
    };

    fetchMessages();

    // Mettre en place un intervalle pour rafraîchir les messages
    const interval = setInterval(fetchMessages, 5000); // Rafraîchir toutes les 5 secondes

    // Nettoyer l'intervalle lors du démontage du composant
    return () => clearInterval(interval);
  }, []);

  const handleContent = (event) => {
    setContent(event.target.value);
  };

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      if (file) {
        // Gestion de l'envoi de fichier
        const formData = new FormData();
        formData.append('file', file);
        const uploadResponse = await uploadFile(formData);

        const fileMessage = {
          type: "FILE",
          sender: username,
          content: content.trim(),
          fileContent: {
            fileName: file.name,
            fileUrl: uploadResponse.data,
            fileType: file.type
          },
          timestamp: new Date().toISOString()
        };

        await publishMessage("/app/sendMessage", fileMessage);
        setFile(null);
      } else if (content.trim()) {
        // Envoi d'un message texte normal
        const chatMessage = {
          type: "CHAT",
          sender: username,
          content: content.trim(),
          timestamp: new Date().toISOString()
        };

        await publishMessage("/app/sendMessage", chatMessage);
      }

      // Réinitialiser le formulaire
      setContent("");

      // Recharger les messages
      const updatedMessages = await axios.get("/api/messages");
      setMessages(updatedMessages.data);
    } catch (error) {
      console.error("Error sending message:", error);
      alert("Failed to send message. Please try again.");
    }
  };

  const renderMessage = (message) => {
    return (
      <div className="chat-message-wrapper pb-2" key={message.id || message.timestamp}>
        <div className="message-content">
          <div className="avatar">
            {message.sender?.charAt(0).toUpperCase() || message.user?.username?.charAt(0).toUpperCase()}
          </div>
          <div className="message-bubble bg-light rounded py-2 px-3 mb-2">
            <div className="message-sender fw-bold">
              {message.sender || message.user?.username}
            </div>
            {message.type === "FILE" ? (
              <div className="file-content">
                {message.fileContent && isImageFile(message.fileContent.fileName) ? (
                  <img
                    src={message.fileContent.fileUrl}
                    alt="Image envoyée"
                    style={{ maxWidth: "200px", maxHeight: "200px" }}
                  />
                ) : (
                  <a
                    href={message.fileContent?.fileUrl}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    {message.fileContent?.fileName || "Télécharger le fichier"}
                  </a>
                )}
              </div>
            ) : (
              <div className="message-text">{message.content}</div>
            )}
          </div>
          <div className="message-time text-muted small">
            {formatTimestamp(message.timestamp)}
          </div>
        </div>
      </div>
    );
  };

  // Fonction utilitaire pour vérifier si un fichier est une image
  const isImageFile = (fileName) => {
    const imageExtensions = ['.jpg', '.jpeg', '.png', '.gif'];
    return imageExtensions.some(ext => fileName.toLowerCase().endsWith(ext));
  };

  // Fonction utilitaire pour formater le timestamp
  const formatTimestamp = (timestamp) => {
    if (!timestamp) return 'Unknown time';
    return new Date(timestamp).toLocaleTimeString();
  };

  return (
    <div className="col-8 card">
      <Profile />
      <div className="chat-messages p-4">
        {messages.map(renderMessage)}
      </div>

      <form onSubmit={handleSubmit} className="chat-input flex-grow-0 py-3 px-4 border-top">
        <div className="input-group">
          <input
            type="text"
            className="form-control"
            placeholder="Type your message"
            value={content}
            onChange={handleContent}
          />
          <input
            type="file"
            className="form-control"
            onChange={handleFileChange}
            accept="image/*,.pdf,.doc,.docx"
          />
          <button
            type="submit"
            className="btn btn-primary"
            disabled={!content.trim() && !file}
          >
            Send
          </button>
        </div>
      </form>
    </div>
  );
};

export default ChatConsole;
