import React, { useState, useEffect } from "react";
import Profile from "./Profile";
import { useParams } from "react-router-dom";
import { useSocket } from "../hooks/useSocket";
import axios from "axios";

const ChatConsole = () => {
  const { username } = useParams();
  const { messages, sendMessage } = useSocket(username);
  const [content, setContent] = useState("");
  const [file, setFile] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const response = await axios.get(`/api/messages/user/${username}`);
        // Ajouter les messages existants à l'état local
        setMessages((prevMessages) => [...response.data, ...prevMessages]);
      } catch (error) {
        console.error("Error fetching messages:", error);
        setError("Failed to fetch messages");
      }
    };

    fetchMessages();
  }, [username]);

  const handleFileUpload = async (file) => {
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      return response.data;
    } catch (error) {
      throw new Error("File upload failed");
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setError(null);

    try {
      let fileUrl = null;
      let fileType = null;

      if (file) {
        const uploadResponse = await handleFileUpload(file);
        fileUrl = uploadResponse.fileUrl;
        fileType = uploadResponse.fileType;
      }

      const chatMessage = {
        type: file ? "FILE" : "CHAT",
        sender: username,
        content: file ? file.name : content.trim(),
        timestamp: new Date().toISOString(),
        fileContent: fileUrl,
        fileType: fileType
      };

      if (content.trim() || file) {
        sendMessage(chatMessage);
        setContent("");
        setFile(null);
      }
    } catch (error) {
      setError("Failed to send message: " + error.message);
    }
  };

  const renderMessage = (message) => (
    <div className="chat-message-wrapper pb-2" key={message.id}>
      <div className="message-content">
        <div className="avatar">
          {message.sender?.charAt(0).toUpperCase()}
        </div>
        <div className="message-bubble bg-light rounded py-2 px-3 mb-2">
          <div className="message-sender fw-bold">{message.sender}</div>
          {message.type === "FILE" ? (
            <div className="file-content">
              {message.fileType === "image" ? (
                <img
                  src={message.fileContent}
                  alt={message.content}
                  className="img-fluid"
                  style={{ maxWidth: "200px" }}
                />
              ) : (
                <a
                  href={message.fileContent}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="btn btn-sm btn-primary"
                >
                  Download {message.content}
                </a>
              )}
            </div>
          ) : (
            <div className="message-text">{message.content}</div>
          )}
          <div className="message-time text-muted small">
            {new Date(message.timestamp).toLocaleTimeString()}
          </div>
        </div>
      </div>
    </div>
  );

  return (
    <div className="col-8 card">
      <Profile />
      <div className="chat-messages p-4">
        {messages.map(renderMessage)}
      </div>
      {error && <div className="alert alert-danger">{error}</div>}
      <form onSubmit={handleSubmit} className="chat-input p-3 border-top">
        <div className="input-group">
          <input
            type="text"
            className="form-control"
            placeholder="Type your message"
            value={content}
            onChange={(e) => setContent(e.target.value)}
          />
          <input
            type="file"
            className="form-control"
            onChange={(e) => setFile(e.target.files[0])}
            accept=".jpg,.jpeg,.png,.gif,.pdf"
          />
          <button
            type="submit"
            className="btn btn-primary"
            disabled={!content.trim() && !file}
          >
            Send
          </button>
        </div>
      </form>
    </div>
  );
};

export default ChatConsole;
