import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { useMessageContext } from "../proxy/StompProvider";
import { uploadFile } from "../component/fileService";
import axios from "axios";

const ChatConsole = () => {
  const { username } = useParams();
  const { publishMessage } = useMessageContext();
  const [messages, setMessages] = useState([]);
  const [content, setContent] = useState("");
  const [file, setFile] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchMessages();

    const interval = setInterval(fetchMessages, 5000);
    return () => clearInterval(interval);
  }, [username]);

  const fetchMessages = async () => {
    try {
      const response = await axios.get(`/api/messages/user/${username}`);
      setMessages(response.data);
    } catch (error) {
      console.error("Erreur lors de la récupération des messages :", error.message);
    }
  };

  const handleContent = (event) => setContent(event.target.value);

  const handleFileChange = (event) => setFile(event.target.files[0]);

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      let chatMessage = {
        type: file ? "FILE" : "CHAT",
        sender: username,
        content: content.trim(),
        timestamp: new Date().toISOString(),
      };

      if (file) {
        const formData = new FormData();
        formData.append('file', file);

        const uploadResponse = await uploadFile(file);
        if (uploadResponse?.fileName) {
          chatMessage.fileContent = {
            fileName: uploadResponse.fileName,
            fileDownloadUri: uploadResponse.fileDownloadUri,
            fileType: uploadResponse.fileType,
          };
        } else {
          throw new Error("Échec de l'envoi du fichier.");
        }
      }

      if (content.trim() || file) {
        await publishMessage("/app/sendMessage", chatMessage);
        setContent("");
        setFile(null);
        fetchMessages();
      }
    } catch (error) {
      console.error("Erreur lors de l'envoi du message :", error.message);
      setError("Impossible d'envoyer le message. Essayez à nouveau.");
    }
  };

  const isImageFile = (fileName) => /\.(jpg|jpeg|png|gif)$/i.test(fileName);

  const renderMessage = (message) => (
    <div key={message.id || message.timestamp} className="chat-message">
      <div className="message-header">
        <strong>{message.sender}</strong>
        <span>{new Date(message.timestamp).toLocaleTimeString()}</span>
      </div>
      <div className="message-content">
        {message.type === "FILE" ? (
          <a
            href={message.fileContent?.fileDownloadUri}
            target="_blank"
            rel="noopener noreferrer"
          >
            {message.fileContent?.fileName || "Télécharger le fichier"}
          </a>
        ) : (
          <p>{message.content}</p>
        )}
      </div>
    </div>
  );

  return (
    <div className="chat-console">
      <div className="messages">{messages.map(renderMessage)}</div>
      <form onSubmit={handleSubmit} className="message-input">
        <input
          type="text"
          value={content}
          onChange={handleContent}
          placeholder="Tapez un message"
        />
        <input type="file" onChange={handleFileChange} />
        <button type="submit" disabled={!content.trim() && !file}>
          Envoyer
        </button>
      </form>
      {error && <p className="error">{error}</p>}
    </div>
  );
};

export default ChatConsole;
