import React, { useState, useEffect } from "react";
import Profile from "./Profile";
import { useParams } from "react-router-dom";
import { useMessageContext } from "../proxy/StompProvider";
import axios from "axios";

const ChatConsole = () => {
  const { username } = useParams();
  const { publishMessage } = useMessageContext();
  const [messages, setMessages] = useState([]);
  const [content, setContent] = useState("");
  const [file, setFile] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const response = await axios.get(`/api/messages/user/${username}`);
        setMessages(response.data);
      } catch (error) {
        console.error("Error fetching messages:", error);
        setError("Failed to fetch messages");
      }
    };

    fetchMessages();
    const interval = setInterval(fetchMessages, 5000);
    return () => clearInterval(interval);
  }, [username]);

  const handleFileUpload = async (file) => {
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      return response.data;
    } catch (error) {
      throw new Error("File upload failed");
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setError(null);

    try {
      let fileUrl = null;
      let fileType = null;

      if (file) {
        const uploadResponse = await handleFileUpload(file);
        fileUrl = uploadResponse.fileUrl;
        fileType = uploadResponse.fileType;
      }

      const chatMessage = {
        type: file ? "FILE" : "CHAT",
        sender: username,
        content: file ? file.name : content.trim(),
        timestamp: new Date().toISOString(),
        fileContent: fileUrl,
        fileType: fileType
      };

      if (content.trim() || file) {
        await publishMessage("/app/sendMessage", chatMessage);
        setContent("");
        setFile(null);
      }
    } catch (error) {
      setError("Failed to send message: " + error.message);
    }
  };

  const renderFileContent = (message) => {
    const isPDF = message.fileType === "pdf";

    return isPDF ? (
      <div className="pdf-viewer">
        <object
          data={message.fileContent}
          type="application/pdf"
          className="w-full h-96"
        >
          <div className="pdf-fallback">
            <p>Unable to display PDF directly.</p>
            <a
              href={message.fileContent}
              target="_blank"
              rel="noopener noreferrer"
              className="btn btn-primary btn-sm"
            >
              Open PDF
            </a>
          </div>
        </object>
      </div>
    ) : (
      <div className="image-viewer">
        <img
          src={message.fileContent}
          alt={message.content}
          className="rounded max-w-md h-auto"
          style={{ maxHeight: "300px" }}
          onClick={() => window.open(message.fileContent, '_blank')}
        />
      </div>
    );
  };

  const renderMessage = (message) => (
    <div className="chat-message-wrapper pb-2" key={message.id}>
      <div className="message-content">
        <div className="avatar">
          {message.sender?.charAt(0).toUpperCase()}
        </div>
        <div className="message-bubble bg-light rounded py-2 px-3 mb-2">
          <div className="message-sender fw-bold">{message.sender}</div>
          {message.type === "FILE" ? (
            <div className="file-content">
              {renderFileContent(message)}
              <div className="file-name text-sm text-gray-600 mt-1">
                {message.content}
              </div>
            </div>
          ) : (
            <div className="message-text">{message.content}</div>
          )}
        </div>
        <div className="message-time text-muted small">
          {new Date(message.timestamp).toLocaleTimeString()}
        </div>
      </div>
    </div>
  );

  return (
    <div className="col-8 card">
      <Profile />
      <div className="chat-messages p-4">
        {messages.map(renderMessage)}
      </div>
      {error && <div className="alert alert-danger">{error}</div>}
      <form onSubmit={handleSubmit} className="chat-input p-3 border-top">
        <div className="input-group">
          <input
            type="text"
            className="form-control"
            placeholder="Type your message"
            value={content}
            onChange={(e) => setContent(e.target.value)}
          />
          <input
            type="file"
            className="form-control"
            onChange={(e) => setFile(e.target.files[0])}
            accept=".jpg,.jpeg,.png,.gif,.pdf"
          />
          <button
            type="submit"
            className="btn btn-primary"
            disabled={!content.trim() && !file}
          >
            Send
          </button>
        </div>
      </form>
    </div>
  );
};

export default ChatConsole;
