import React, { useState } from "react";
import Profile from "./Profile";
import { useParams } from "react-router-dom";
import { useSelector } from "react-redux";
import { useMessageContext } from "../proxy/StompProvider";
import { uploadFile } from "../component/fileService"; // Assurez-vous que ce chemin est correct

const ChatConsole = () => {
  const { username } = useParams();
  const messages = useSelector((state) => state.messageState.messages || []);
  const [content, setContent] = useState("");
  const [file, setFile] = useState(null); // Nouvel état pour gérer les fichiers
  const { publishMessage } = useMessageContext();

  const handleContent = (event) => {
    setContent(event.target.value);
  };

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      // Si un fichier est sélectionné, envoyez-le d'abord
      if (file) {
        const response = await uploadFile(file);
        const fileMessage = {
          type: "FILE",
          sender: username,
          content: response.data, // L'URL du fichier renvoyée par le backend
          timestamp: new Date().toISOString(),
        };
        await publishMessage("/app/sendMessage", fileMessage);
        setFile(null); // Réinitialiser l'état du fichier après l'envoi
      }

      // Envoyer le message texte si le champ de contenu n'est pas vide
      if (content.trim()) {
        const message = {
          type: "CHAT",
          sender: username,
          content: content.trim(),
          timestamp: new Date().toISOString(),
        };
        await publishMessage("/app/sendMessage", message);
        setContent(""); // Réinitialiser le champ de message
      }
    } catch (error) {
      console.error("Error sending message or file:", error);
      alert("Failed to send message or file. Please try again.");
    }
  };

  return (
    <div className="col-8 card">
      <Profile />
      <div className="chat-messages p-4">
        {messages.map((message, index) => (
          <div className="chat-message-wrapper pb-2" key={`${message.sender}-${index}`}>
            {message.sender && (
              <div className="message-content">
                <div className="avatar">{message.sender.charAt(0).toUpperCase()}</div>
                <div className="message-bubble bg-light rounded py-2 px-3 mb-2">
                  <div className="message-sender fw-bold">{message.sender}</div>
                  {/* Affichage des fichiers et des messages texte */}
                  {message.type === "FILE" ? (
                    <>
                      {/* Affichage d'une image si c'est un fichier image */}
                      {message.content.endsWith(".jpg") ||
                      message.content.endsWith(".jpeg") ||
                      message.content.endsWith(".png") ||
                      message.content.endsWith(".gif") ? (
                        <img
                          src={message.content} // URL de l'image
                          alt="Image envoyée"
                          style={{ maxWidth: "200px", maxHeight: "200px" }}
                        />
                      ) : (
                        // Lien de téléchargement pour les autres fichiers
                        <a href={message.content} target="_blank" rel="noopener noreferrer">
                          Télécharger le fichier
                        </a>
                      )}
                    </>
                  ) : (
                    <div className="message-text">{message.content}</div>
                  )}
                </div>
                <div className="message-time text-muted small">
                  {message.timestamp
                    ? new Date(message.timestamp).toLocaleTimeString()
                    : "Unknown time"}
                </div>
              </div>
            )}
          </div>
        ))}
      </div>

      <form onSubmit={handleSubmit}>
        <div className="chat-input flex-grow-0 py-3 px-4 border-top">
          <div className="input-group">
            <input
              type="text"
              className="form-control"
              placeholder="Type your message"
              aria-label="Message input"
              value={content}
              onChange={handleContent}
            />
            <input
              type="file"
              className="form-control"
              onChange={handleFileChange}
              aria-label="File input"
            />
            <button
              type="submit"
              className="btn btn-primary"
              aria-label="Send message"
              disabled={!content.trim() && !file}
            >
              Send
            </button>
          </div>
        </div>
      </form>
    </div>
  );
};

export default ChatConsole;
