import React, { useState, useEffect } from "react";
import Profile from "./Profile";
import { useParams } from "react-router-dom";
import { useMessageContext } from "../proxy/StompProvider";
import { uploadFile } from "../component/fileService";
import axios from "axios";

const ChatConsole = () => {
  const { username } = useParams();
  const { publishMessage } = useMessageContext();
  const [messages, setMessages] = useState([]); // Correction : unique état pour `messages`
  const [content, setContent] = useState("");
  const [file, setFile] = useState(null);

  // Charger les messages depuis le backend
  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const response = await axios.get("/api/messages");
        setMessages(response.data);
      } catch (error) {
        console.error("Error fetching messages:", error);
      }
    };

    fetchMessages();
  }, []);

  const handleContent = (event) => {
    setContent(event.target.value);
  };

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      // Si un fichier est sélectionné, envoyez-le d'abord
      if (file) {
        const response = await uploadFile(file);
        const fileMessage = {
          type: "FILE",
          sender: username,
          content: response.data, // URL du fichier
          timestamp: new Date().toISOString(),
        };
        await publishMessage("/app/sendMessage", fileMessage);
        setFile(null);
      }

      // Envoyer le message texte
      if (content.trim()) {
        const textMessage = {
          type: "CHAT",
          sender: username,
          content: content.trim(),
          timestamp: new Date().toISOString(),
        };
        await publishMessage("/app/sendMessage", textMessage);
        setContent(""); // Réinitialiser le champ texte
      }

      // Recharger les messages après envoi
      const updatedMessages = await axios.get("/api/messages");
      setMessages(updatedMessages.data);
    } catch (error) {
      console.error("Error sending message or file:", error);
      alert("Failed to send message or file. Please try again.");
    }
  };

  return (
    <div className="col-8 card">
      <Profile />
      <div className="chat-messages p-4">
        {messages.map((message, index) => (
          <div className="chat-message-wrapper pb-2" key={index}>
            <div className="message-content">
              <div className="avatar">{message.sender.charAt(0).toUpperCase()}</div>
              <div className="message-bubble bg-light rounded py-2 px-3 mb-2">
                <div className="message-sender fw-bold">{message.sender}</div>
                {message.type === "FILE" ? (
                  message.content.endsWith(".jpg") ||
                  message.content.endsWith(".jpeg") ||
                  message.content.endsWith(".png") ||
                  message.content.endsWith(".gif") ? (
                    <img
                      src={message.content}
                      alt="Image envoyée"
                      style={{ maxWidth: "200px", maxHeight: "200px" }}
                    />
                  ) : (
                    <a href={message.content} target="_blank" rel="noopener noreferrer">
                      Télécharger le fichier
                    </a>
                  )
                ) : (
                  <div className="message-text">{message.content}</div>
                )}
              </div>
              <div className="message-time text-muted small">
                {message.timestamp
                  ? new Date(message.timestamp).toLocaleTimeString()
                  : "Unknown time"}
              </div>
            </div>
          </div>
        ))}
      </div>

      <form onSubmit={handleSubmit}>
        <div className="chat-input flex-grow-0 py-3 px-4 border-top">
          <div className="input-group">
            <input
              type="text"
              className="form-control"
              placeholder="Type your message"
              value={content}
              onChange={handleContent}
            />
            <input type="file" className="form-control" onChange={handleFileChange} />
            <button
              type="submit"
              className="btn btn-primary"
              disabled={!content.trim() && !file}
            >
              Send
            </button>
          </div>
        </div>
      </form>
    </div>
  );
};

export default ChatConsole;
