import axios from 'axios';

const API_URL = '/api/files';

export const uploadFile = async (file) => {
  const formData = new FormData();
  formData.append('file', file);

  try {
    const response = await axios.post(`${API_URL}/envoyer`, formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    });
    console.log('File upload successful:', response.data);
    return response.data;
  } catch (error) {
    console.error('Error uploading file:', error.response ? error.response.data : error.message);
    throw error;
  }
};

export const getAllFiles = async () => {
  try {
    const response = await axios.get(`${API_URL}/files`);
    console.log('Retrieved all files:', response.data);
    return response.data;
  } catch (error) {
    console.error('Error getting all files:', error.response ? error.response.data : error.message);
    throw error;
  }
};

export const downloadFile = async (filename) => {
  try {
    const response = await axios.get(`${API_URL}/files/${filename}`, {
      responseType: 'blob',
    });
    console.log('File download successful:', filename);
    return response.data;
  } catch (error) {
    console.error('Error downloading file:', error.response ? error.response.data : error.message);
    throw error;
  }
};
