const ChatConsole = () => {
  const { username } = useParams();
  const { publishMessage, subscribe } = useMessageContext();
  const [messages, setMessages] = useState([]);
  const [content, setContent] = useState("");
  const [file, setFile] = useState(null);
  const [error, setError] = useState(null);
  const [isUploading, setIsUploading] = useState(false);

  useEffect(() => {
    const subscription = subscribe("/topic/messages", (message) => {
      setMessages(prev => [...prev, message]);
    });

    fetchMessages();
    return () => subscription.unsubscribe();
  }, [username]);

  const fetchMessages = async () => {
    try {
      const { data } = await axios.get(`/api/messages/user/${username}`);
      setMessages(data);
    } catch (error) {
      setError("Failed to fetch messages");
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setError(null);
    setIsUploading(true);

    try {
      let messageData = {
        type: file ? "FILE" : "CHAT",
        sender: username,
        content: content.trim(),
        timestamp: new Date().toISOString()
      };

      if (file) {
        const formData = new FormData();
        formData.append('file', file);
        const { data: fileData } = await axios.post('/api/files/upload', formData);

        messageData = {
          ...messageData,
          fileInfo: {
            fileName: fileData.fileName,
            fileDownloadUri: fileData.fileDownloadUri,
            fileType: fileData.fileType
          }
        };
      }

      await publishMessage("/app/sendMessage", messageData);
      setContent("");
      setFile(null);
    } catch (error) {
      setError(`Error: ${error.message}`);
    } finally {
      setIsUploading(false);
    }
  };

  const downloadFile = async (fileInfo) => {
    try {
      const response = await axios.get(fileInfo.fileDownloadUri, {
        responseType: 'blob'
      });

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.download = fileInfo.fileName;
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(url);
    } catch (error) {
      setError("Download failed");
    }
  };

  const renderMessage = (message) => (
    <div className="chat-message-wrapper pb-2" key={message.id || message.timestamp}>
      <div className="message-content">
        <div className="avatar">
          {message.sender?.charAt(0).toUpperCase()}
        </div>
        <div className="message-bubble bg-light rounded py-2 px-3 mb-2">
          <div className="message-sender fw-bold">{message.sender}</div>
          {message.type === "FILE" ? (
            <div className="file-content">
              {message.fileInfo && isImageFile(message.fileInfo.fileName) ? (
                <img
                  src={message.fileInfo.fileDownloadUri}
                  alt="Uploaded"
                  className="img-fluid"
                  style={{ maxWidth: "200px" }}
                />
              ) : (
                <button
                  className="btn btn-link"
                  onClick={() => downloadFile(message.fileInfo)}
                >
                  📎 {message.fileInfo?.fileName}
                </button>
              )}
            </div>
          ) : (
            <div className="message-text">{message.content}</div>
          )}
        </div>
      </div>
    </div>
  );

  return (
    <div className="col-8 card">
      <Profile />
      <div className="chat-messages p-4">
        {messages.map(renderMessage)}
      </div>
      {error && <div className="alert alert-danger">{error}</div>}
      <form onSubmit={handleSubmit} className="chat-input p-3 border-top">
        <div className="input-group">
          <input
            type="text"
            className="form-control"
            value={content}
            onChange={e => setContent(e.target.value)}
            placeholder="Type your message"
          />
          <input
            type="file"
            className="form-control"
            onChange={e => setFile(e.target.files[0])}
            accept="image/*,.pdf,.doc,.docx"
          />
          <button
            type="submit"
            className="btn btn-primary"
            disabled={isUploading || (!content.trim() && !file)}
          >
            {isUploading ? 'Sending...' : 'Send'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default ChatConsole;
